//import './MainPage.css'
import App from "../";

const RootStyle  = styled.div`
  background:#DC143C;
  height:100vh;
`



const MainPage = () => {

const [src,setSrc] = useState();

  useEffect(()=>{
    setSrc("../Assets/adv-aseets/adv-form-banner.png")
  })



  return(
    <RootStyle>
        <h2>Teknolojik Yemekler</h2>
        <h1>KOD ACIKTIRIR <br/>
            PIZZA, DOYURUR
        </h1>
        <Button>ACIKTIM</Button>
        <div>
          <img src={"../Assets/adv-aseets/adv-form-banner.png"}
          alt ="Ana Sayfa Pizza"/>
        </div>
     </RootStyle>
  );
}

export default MainPage;
